// Convert JS string array to Postgres TEXT[] literal (e.g. {"a","b"})
function pgTextArray(arr: string[]): string {
  return '{' + arr.map(s => '"' + s.replace(/\\/g, '\\\\').replace(/"/g, '\\"') + '"').join(',') + '}'
}

// ==================== SM-2 SPACED REPETITION ALGORITHM ====================

function calculateNextReview(
  easeFactor: number,
  interval: number,
  repetitions: number,
  result: 'again' | 'hard' | 'good' | 'easy'
): { nextInterval: number; easeFactor: number; repetitions: number; status: string } {
  const quality = { again: 0, hard: 2, good: 3, easy: 5 }[result]

  let newEF = easeFactor + (0.1 - (5 - quality) * (0.08 + (5 - quality) * 0.02))
  newEF = Math.max(1.3, newEF)

  let newInterval: number
  let newReps: number
  let status: string

  if (quality < 2) {
    // Failed — restart learning
    newInterval = 0
    newReps = 0
    status = 'relearning'
  } else {
    newReps = repetitions + 1
    if (newReps === 1) {
      newInterval = 1
    } else if (newReps === 2) {
      newInterval = 6
    } else {
      newInterval = Math.round(interval * newEF)
    }
    status = 'review'
  }

  if (result === 'easy') {
    newInterval = Math.max(newInterval, Math.round(interval * 2.5) || 4)
  }

  return { nextInterval: newInterval, easeFactor: newEF, repetitions: newReps, status }
}

