// ==================== IN-MEMORY FALLBACK STORAGE ====================

let fallbackCards = [...SEED_CARDS]
let fallbackProgress: Record<string, {
  ease_factor: number
  interval: number
  repetitions: number
  last_reviewed: string | null
  next_review: string | null
  status: string
}> = {}

function initFallbackProgress() {
  const today = new Date().toISOString().split('T')[0]
  for (const card of fallbackCards) {
    if (!fallbackProgress[card.id]) {
      fallbackProgress[card.id] = {
        ease_factor: 2.5,
        interval: 0,
        repetitions: 0,
        last_reviewed: null,
        next_review: today,
        status: 'new'
      }
    }
  }
}

// ==================== POSTGRES FUNCTIONS ====================

let tablesInitialized = false

async function initTables() {
  if (tablesInitialized) return
  try {
    await query(
      `CREATE TABLE IF NOT EXISTS spanish_cards (
        id VARCHAR(50) PRIMARY KEY,
        spanish_word VARCHAR(255) NOT NULL,
        english_translation TEXT NOT NULL,
        example_sentence_spanish TEXT,
        example_sentence_english TEXT,
        word_type VARCHAR(50),
        tags TEXT[] DEFAULT '{}',
        source VARCHAR(100),
        created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
      )`
    )
    await query(
      `CREATE TABLE IF NOT EXISTS spanish_progress (
        card_id VARCHAR(50) PRIMARY KEY REFERENCES spanish_cards(id),
        ease_factor FLOAT DEFAULT 2.5,
        interval INTEGER DEFAULT 0,
        repetitions INTEGER DEFAULT 0,
        last_reviewed TIMESTAMP,
        next_review TIMESTAMP,
        status VARCHAR(20) DEFAULT 'new'
      )`
    )
    await query('CREATE INDEX IF NOT EXISTS idx_sp_next_review ON spanish_progress(next_review)')
    tablesInitialized = true

    // Auto-seed: if the cards table is empty, populate from SEED_CARDS
    const countResult = await query('SELECT COUNT(*) as count FROM spanish_cards')
    const cardCount = Number(countResult.rows[0]?.count || 0)
    if (cardCount === 0) {
      // Auto-seeding Spanish cards into Postgres
      for (const card of SEED_CARDS) {
        await query(
          'INSERT INTO spanish_cards (id, spanish_word, english_translation, example_sentence_spanish, example_sentence_english, word_type, tags, source) VALUES ($1, $2, $3, $4, $5, $6, $7, $8) ON CONFLICT (id) DO NOTHING',
          [card.id, card.spanish_word, card.english_translation, card.example_sentence_spanish, card.example_sentence_english, card.word_type, pgTextArray(card.tags), card.source]
        )
        await query('INSERT INTO spanish_progress (card_id) VALUES ($1) ON CONFLICT (card_id) DO NOTHING', [card.id])
      }
      // Auto-seed complete
    }
  } catch (error) {
    console.error('Failed to init Spanish tables:', error)
    throw error
  }
}

// ==================== API HANDLERS ====================

export async function GET(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url)
    const action = searchParams.get('action') || 'due'
    const limit = parseInt(searchParams.get('limit') || '20')

    initFallbackProgress()

    if (action === 'due') {
      // Get cards due for review
      const today = new Date().toISOString().split('T')[0]

      if (usePostgres) {
        try {
          await initTables()
          const result = await query(
            `SELECT c.*,
              COALESCE(p.ease_factor, 2.5) as ease_factor,
              COALESCE(p.interval, 0) as interval,
              COALESCE(p.repetitions, 0) as repetitions,
              COALESCE(p.status, 'new') as status,
              p.next_review,
              p.last_reviewed
            FROM spanish_cards c
            LEFT JOIN spanish_progress p ON c.id = p.card_id
            WHERE (p.status IS NULL OR p.status != 'parked') AND (p.next_review IS NULL OR p.next_review::date <= $1::date)
            ORDER BY p.next_review ASC NULLS FIRST
            LIMIT $2`,
            [today, limit]
          )
          return NextResponse.json({ cards: result.rows, total: result.rowCount })
        } catch (error) {
          console.error('Postgres error:', error)
        }
      }

      // Fallback
      const dueCards = fallbackCards
        .filter(card => {
          const progress = fallbackProgress[card.id]
          return progress && progress.status !== 'parked' && (
            !progress.next_review || progress.next_review <= today
          )
        })
        .slice(0, limit)
        .map(card => ({ ...card, ...fallbackProgress[card.id] }))

      return NextResponse.json({ cards: dueCards, total: dueCards.length })
    }

    if (action === 'progress') {
      // Get overall progress stats
      if (usePostgres) {
        try {
          await initTables()
          const stats = await query(
            `SELECT
              COUNT(*) FILTER (WHERE status = 'new') as new_count,
              COUNT(*) FILTER (WHERE status = 'learning' OR status = 'relearning') as learning_count,
              COUNT(*) FILTER (WHERE status = 'review') as review_count,
              COUNT(*) FILTER (WHERE status = 'parked') as parked_count,
              COUNT(*) as total
            FROM spanish_progress`
          )
          const todayReviewed = await query(
            'SELECT COUNT(*) as count FROM spanish_progress WHERE last_reviewed::date = CURRENT_DATE'
          )
          return NextResponse.json({
            stats: stats.rows[0],
            reviewedToday: todayReviewed.rows[0]?.count || 0
          })
        } catch (error) {
          console.error('Postgres error:', error)
        }
      }

      // Fallback stats
      const values = Object.values(fallbackProgress)
      const today = new Date().toISOString().split('T')[0]
      return NextResponse.json({
        stats: {
          new_count: values.filter(p => p.status === 'new').length,
          learning_count: values.filter(p => p.status === 'learning' || p.status === 'relearning').length,
          review_count: values.filter(p => p.status === 'review').length,
          parked_count: values.filter(p => p.status === 'parked').length,
          total: values.length
        },
        reviewedToday: values.filter(p => p.last_reviewed === today).length
      })
    }

    if (action === 'all') {
      // Get all cards with progress
      if (usePostgres) {
        try {
          await initTables()
          const result = await query(
            `SELECT c.*,
              COALESCE(p.ease_factor, 2.5) as ease_factor,
              COALESCE(p.interval, 0) as interval,
              COALESCE(p.repetitions, 0) as repetitions,
              COALESCE(p.status, 'new') as status,
              p.next_review,
              p.last_reviewed
            FROM spanish_cards c
            LEFT JOIN spanish_progress p ON c.id = p.card_id
            ORDER BY c.spanish_word`
          )
          return NextResponse.json({ cards: result.rows })
        } catch (error) {
          console.error('Postgres error:', error)
        }
      }

      return NextResponse.json({
        cards: fallbackCards.map(c => ({ ...c, ...fallbackProgress[c.id] }))
      })
    }

    return NextResponse.json({ error: 'Unknown action' }, { status: 400 })
  } catch (error) {
    console.error('Spanish GET error:', error)
    return NextResponse.json({ error: 'Failed to load' }, { status: 500 })
  }
}

export async function POST(request: NextRequest) {
  try {
    const body = await request.json()
    const { action } = body

    initFallbackProgress()

    if (action === 'review') {
      const { cardId, result } = body
      if (!cardId || !result) {
        return NextResponse.json({ error: 'cardId and result required' }, { status: 400 })
      }

      const progress = fallbackProgress[cardId] || { ease_factor: 2.5, interval: 0, repetitions: 0 }
      const sr = calculateNextReview(progress.ease_factor, progress.interval, progress.repetitions, result)
      const now = new Date()
      const nextReview = new Date(now.getTime() + sr.nextInterval * 86400000)

      if (usePostgres) {
        try {
          await initTables()
          await query(
            'INSERT INTO spanish_progress (card_id, ease_factor, interval, repetitions, last_reviewed, next_review, status) VALUES ($1, $2, $3, $4, NOW(), $5, $6) ON CONFLICT (card_id) DO UPDATE SET ease_factor = EXCLUDED.ease_factor, interval = EXCLUDED.interval, repetitions = EXCLUDED.repetitions, last_reviewed = EXCLUDED.last_reviewed, next_review = EXCLUDED.next_review, status = EXCLUDED.status',
            [cardId, sr.easeFactor, sr.nextInterval, sr.repetitions, nextReview.toISOString(), sr.status]
          )
          return NextResponse.json({ success: true, nextReview: nextReview.toISOString(), interval: sr.nextInterval })
        } catch (error) {
          console.error('Postgres error:', error)
        }
      }

      // Fallback
      fallbackProgress[cardId] = {
        ease_factor: sr.easeFactor,
        interval: sr.nextInterval,
        repetitions: sr.repetitions,
        last_reviewed: now.toISOString().split('T')[0],
        next_review: nextReview.toISOString().split('T')[0],
        status: sr.status
      }
      return NextResponse.json({ success: true, nextReview: nextReview.toISOString(), interval: sr.nextInterval })
    }

    if (action === 'park') {
      const { cardId } = body
      if (!cardId) return NextResponse.json({ error: 'cardId required' }, { status: 400 })

      if (usePostgres) {
        try {
          await initTables()
          await query(
            "INSERT INTO spanish_progress (card_id, status) VALUES ($1, 'parked') ON CONFLICT (card_id) DO UPDATE SET status = 'parked'",
            [cardId]
          )
          return NextResponse.json({ success: true, status: 'parked' })
        } catch (error) {
          console.error('Postgres error:', error)
        }
      }

      if (fallbackProgress[cardId]) {
        fallbackProgress[cardId].status = 'parked'
      }
      return NextResponse.json({ success: true, status: 'parked' })
    }

    if (action === 'unpark') {
      const { cardId } = body
      if (!cardId) return NextResponse.json({ error: 'cardId required' }, { status: 400 })

      const today = new Date().toISOString().split('T')[0]
      if (usePostgres) {
        try {
          await initTables()
          await query(
            "UPDATE spanish_progress SET status = 'review', next_review = $1 WHERE card_id = $2",
            [today, cardId]
          )
          return NextResponse.json({ success: true, status: 'review' })
        } catch (error) {
          console.error('Postgres error:', error)
        }
      }

      if (fallbackProgress[cardId]) {
        fallbackProgress[cardId].status = 'review'
        fallbackProgress[cardId].next_review = today
      }
      return NextResponse.json({ success: true, status: 'review' })
    }

    if (action === 'add') {
      const { spanish_word, english_translation, example_sentence_spanish, example_sentence_english, word_type, tags } = body
      if (!spanish_word || !english_translation) {
        return NextResponse.json({ error: 'spanish_word and english_translation required' }, { status: 400 })
      }

      const id = `es-custom-${Date.now()}`
      const card = {
        id,
        spanish_word,
        english_translation,
        example_sentence_spanish: example_sentence_spanish || '',
        example_sentence_english: example_sentence_english || '',
        word_type: word_type || 'unknown',
        tags: tags || [],
        source: 'manual'
      }

      if (usePostgres) {
        try {
          await initTables()
          await query(
            'INSERT INTO spanish_cards (id, spanish_word, english_translation, example_sentence_spanish, example_sentence_english, word_type, tags, source) VALUES ($1, $2, $3, $4, $5, $6, $7, $8)',
            [id, spanish_word, english_translation, card.example_sentence_spanish, card.example_sentence_english, card.word_type, pgTextArray(card.tags), card.source]
          )
          await query('INSERT INTO spanish_progress (card_id) VALUES ($1)', [id])
          return NextResponse.json({ success: true, card })
        } catch (error) {
          console.error('Postgres error:', error)
        }
      }

      fallbackCards.push(card)
      fallbackProgress[id] = {
        ease_factor: 2.5, interval: 0, repetitions: 0,
        last_reviewed: null, next_review: new Date().toISOString().split('T')[0],
        status: 'new'
      }
      return NextResponse.json({ success: true, card })
    }

    // Seed cards into postgres if needed
    if (action === 'seed') {
      if (usePostgres) {
        try {
          await initTables()
          for (const card of SEED_CARDS) {
            await query(
              'INSERT INTO spanish_cards (id, spanish_word, english_translation, example_sentence_spanish, example_sentence_english, word_type, tags, source) VALUES ($1, $2, $3, $4, $5, $6, $7, $8) ON CONFLICT (id) DO NOTHING',
              [card.id, card.spanish_word, card.english_translation, card.example_sentence_spanish, card.example_sentence_english, card.word_type, pgTextArray(card.tags), card.source]
            )
            await query('INSERT INTO spanish_progress (card_id) VALUES ($1) ON CONFLICT (card_id) DO NOTHING', [card.id])
          }
          return NextResponse.json({ success: true, seeded: SEED_CARDS.length })
        } catch (error) {
          console.error('Seed error:', error)
        }
      }
      return NextResponse.json({ success: true, message: 'Using in-memory seed data' })
    }

    return NextResponse.json({ error: 'Unknown action' }, { status: 400 })
  } catch (error) {
    console.error('Spanish POST error:', error)
    return NextResponse.json({ error: 'Failed' }, { status: 500 })
  }
}
